INSERT INTO `cache_filter` VALUES ('1:68a586d7cb39b8237253ce247869c45a','<h2>Taking the tutorial for class credit?</h2>\n<p>Find your class(es). Select an option from at least one of the drop-down menus below and click Search. After finding your class(es) select JOIN. This will allow your professor to retrieve your quiz results.</p>\n<h2>Taking the tutorial on your own initiative?</h2>\n<p>You don\'t need to select a course. Proceed to a quiz using the links on the left.</p>\n',1364079641,1363993241,'',0),('2:a1839176ec8e173894b97cf9e4f78ad4','<script type=\"text/javascript\">\n<!--\nvar notSupportedBrowsers = [{\'os\': \'Any\', \'browser\': \'MSIE\', \'version\': 9}, {\'os\': \'Any\', \'browser\': \'Firefox\', \'version\': 11}, {\'os\': \'Any\', \'browser\': \'Chrome\', \'version\': 18}, {\'os\': \'Any\', \'browser\': \'Opera\', \'version\': 11.61}, {\'os\': \'Any\', \'browser\': \'Safari\', \'version\': 5.1}];\nvar displayPoweredBy = false;\n// -->\n</script>',1364079641,1363993241,'',0),('2:71a70458997717bb913edc2ba7e2aca7','<div id=\"menu-uc-libraries\">\n	<ul class=\"menu\">\n		<li class=\"first leaf\">\n			<a href=\"http://www.lib.berkeley.edu/\" title=\"UC Berkeley Library\">UC Berkeley</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://www.lib.ucdavis.edu/\" title=\"UC Davis Library\">UC Davis</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://www.lib.uci.edu/\" title=\"UC Irvine Library\">UC Irvine</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://www.library.ucla.edu/\" title=\"UCLA Library\">UCLA</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://library.ucmerced.edu/\" title=\"UC Merced Library\">UC Merced</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://library.ucr.edu/\" title=\"UC Riverside Library\">UC Riverside</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://libraries.ucsd.edu/\" title=\"UCSD Library\">UCSD</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://www.library.ucsf.edu/\" title=\"UCSF Library\">UCSF</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"http://www.library.ucsb.edu/\" title=\"UC Santa Barbara Library\">UC Santa Barbara</a>\n		</li>\n		<li class=\"last leaf\">\n			<a href=\"http://library.ucsc.edu/\" title=\"UC Santa Cruz Library\">UC Santa Cruz</a>\n		</li>\n	</ul>\n</div>\n\n<div id=\"menu-rpt-sections\">\n	<ul class=\"menu\">\n		<li class=\"first leaf\">\n			<a href=\"/begin\">BEGIN RESEARCH</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"/knowledge_cycle\">KNOWLEDGE CYCLE</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"/books\">FIND BOOKS</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"/articles\">FIND ARTICLES</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"/citations\">MAKE CITATIONS</a>\n		</li>\n		<li class=\"leaf\">\n			<a href=\"/basic_search\">BASIC SEARCH</a>\n		</li>\n		<li class=\"last leaf\">\n			<a href=\"/advanced_search\">ADVANCED SEARCH</a>\n		</li>\n	</ul>\n</div>\n<div class=\"section-arrow\"></div>',1364079641,1363993241,'',0),('2:b56b164c80d87fdc18c80c1166aed1e2','<h2>404 - Page Not Found</h2>\n<div class=\"messages\">\nThe page you requested could not be found.\n</div>',1364079643,1363993243,'',0),('2:ab5b683b8dba87dc85a89ddf170a02ef','<h2>UC Libraries Begin Research Tutorial</h2>\n\n<p><a rel=\"license\" href=\"http://creativecommons.org/licenses/by-nc-sa/3.0/\"><img alt=\"Creative Commons License\" style=\"border-width:0\" src=\"../images/logo-creative-commons-byncsa.png\" /></a></p>\n\n<p><span xmlns:dct=\"http://purl.org/dc/terms/\" property=\"dct:title\">University of California (UC) Libraries \"Begin Research Tutorial\" is licensed under a <a rel=\"license\" href=\"http://creativecommons.org/licenses/by-nc-sa/3.0/\">Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License</a>.</span></p>\n\n<p>This tutorial is based on <a xmlns:dct=\"http://purl.org/dc/terms/\" href=\"http://www.lib.uci.edu/uc-research-tutorial/begin.html\" rel=\"dct:source\">a tutorial hosted at the UC Irvine Libraries</a>.</p>\n\n<p>UC Merced Library staff have transferred the tutorial content to Drupal.  Primary changes have included converting much of the content from Flash to HTML5, using CSS to format content display, and including quizzes for each module from which participation rates and scores can be recorded and retrieved.</p>\n\n<p><strong>Download Source Files</strong><br />\nSource code for this tutorial is available on GitHub at <a href=\"https://github.com/ucmlibrary/ucm-rpt\">https://github.com/ucmlibrary/ucm-rpt</a> </p>\n\n<p>The Creative Commons icon on each tutorial page links to this license information page which contains a link to the Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License description at creativecommons.org as well as a link to download the source files.</p>\n<p></p>\n\n<p><strong>UC Merced Contact</strong><br />\nSara Davidson<br />\nUniversity of California, UC Merced Library<br />\n<a href=\"mailto:sdavidson2@ucmerced.edu\">sdavidson2@ucmerced.edu</a> \n\n</p><p><strong>UC Irvine Contact</strong><br />\nCathy Palmer<br />\nUniversity of California, Irvine Libraries<br />  \n<a href=\"mailto:cpalmer@uci.edu\">cpalmer@uci.edu</a></p>',1364079646,1363993246,'',0);
